package be.yildizgames.sdk.feature.project.model;

/**
 * The author is the name of the project creator.
 * This name will be displayed in the generated files as well as in the licence.
 */
public class Author {

    public final String value;


    public Author(final String name) {
        super();
        if(name == null || name.isEmpty()) {
            throw AuthorValidationException.empty();
        }
        this.value = name;
    }
}
